mapper.select:
SELECT lv.id, lv.title, lv.description, lv.keywords, lv.language,
lv.event_id, lv.slug, lv.place_id
FROM project.event_landing_page_view lv
WHERE
lv.event_id = IF(#{id} = 0, lv.event_id, #{id})

	AND lv.language = #{language.code}


	AND lv.start_date = #{startDate}


	AND lv.end_date = #{endDate}

AND type = 2

SELECT ev.id, ev.place_id, ev.id AS event_id, ev.type, ev.period_type, #{language.code} AS language, ev.start_date, ev.start_time, ev.end_date, ev.end_time, ev.show_start_time, ev.show_end_time, CASE #{language.code} WHEN 'TR' THEN ev.tr_name WHEN 'EN' THEN ev.en_name WHEN 'RU' THEN ev.ru_name WHEN 'DE' THEN ev.de_name END AS name, CASE #{language.code} WHEN 'TR' THEN ev.tr_slug WHEN 'EN' THEN ev.en_slug WHEN 'RU' THEN ev.ru_slug WHEN 'DE' THEN ev.de_slug END AS slug, ev.create_datetime, ev.update_datetime FROM project.event_view ev WHERE ev.id = IF(#{id} = 0, ev.id, #{id})
AND ev.start_date = #{startDate}
AND ev.end_date = #{endDate}
